if( this.view.switchToggle.left == '0dp') {
  this.view.switchToggle.animate(
    kony.ui.createAnimation({
        "100": {
          	"left": "10dp",
            "stepConfig": {
                "timingFunction": kony.anim.EASE
            },
          	"backgroundColor": "002e5f00"
        }
    }), {
        "delay": 0,
        "iterationCount": 1,
        "fillMode": kony.anim.FILL_MODE_FORWARDS,
        "duration": 0.25
    }, {
        "animationEnd": null
    }
);
  this.view.slider.animate(
    kony.ui.createAnimation({
        "100": {
            "stepConfig": {
                "timingFunction": kony.anim.EASE
            },
          	"backgroundColor": "9aabbe00"
        }
    }), {
        "delay": 0,
        "iterationCount": 1,
        "fillMode": kony.anim.FILL_MODE_FORWARDS,
        "duration": 0.25
    }, {
        "animationEnd": null
    }
);
} else {
this.view.switchToggle.animate(
    kony.ui.createAnimation({
        "100": {
            "left": "0dp",
            "stepConfig": {
                "timingFunction": kony.anim.EASE
            },
          	"backgroundColor": "e6e6e600",
        }
    }), {
        "delay": 0,
        "iterationCount": 1,
        "fillMode": kony.anim.FILL_MODE_FORWARDS,
        "duration": 0.25
    }, {
        "animationEnd": null
